import React from 'react';
import './Doctor.css';

function DoctorComponent() {
  const [accountAddress, setAccountAddress] = useState(''); // initial value for account address
  const [patientAddress, setPatientAddress] = useState(''); // initial value for patient address
  const [patientUpdateInfo, setPatientUpdateInfo] = useState(''); // initial value for patient update info

  // Function to read permission
  const readPermission = () => {
    // code to request read permission
  };

  // Function to write permission
  const writePermission = () => {
    // code to request write permission
  };

  // Function to handle file selection
  const onFileSelected = (event) => {
    // code to handle file selection
  };

  // Function to update patient data
  const updatePatientData = () => {
    // code to update patient data
  };

  // Function to get the account address dynamically (replace this with your own code to fetch the address)
  const getAccountAddress = () => {
    const address = '0x123456789'; // example address
    setAccountAddress(address);
  };

  // Call the function to get the account address when the component mounts
  useEffect(() => {
    getAccountAddress();
  }, []);

  return (
    <div className="doctor-body-container">

      <div className="request-approval-container">
        {/* FOR THE LEFT SIDE */}
        <div className="approval-container">
          <h1>Left Side</h1>
        </div>
      </div>

      {/* FOR THE RIGHT SIDE */}
      <div className="profile-container">
        <div className="icon-text-container">
          <div className="account-address">
            {accountAddress}
            <i className="material-icons profile-icon">account_circle</i>
          </div>
        </div>

        <h2>Need Someone's Data?</h2>
        <div className="request-approval">
          <input
            type="text"
            className="address-input"
            placeholder="Enter the address of the patient!"
            value={patientAddress}
            onChange={(e) => setPatientAddress(e.target.value)}
          />
          <div className="button-container">
            <button onClick={readPermission} className="read-button">
              Read Request
            </button>
            <button onClick={writePermission} className="write-button">
              Write Request
            </button>
          </div>
          <input
            type="text"
            className="update-data-input"
            placeholder="Update with the details like medication etc..!"
            value={patientUpdateInfo}
            onChange={(e) => setPatientUpdateInfo(e.target.value)}
          />
          <div className="file-upload-container">
            <input
              type="file"
              className="file-input"
              multiple
              onChange={onFileSelected}
            />
            <label htmlFor="file" className="file-label">
              Choose a file
            </label>
          </div>
        </div>

        <button onClick={updatePatientData} className="update-patient-timeline">
          Update Data
        </button>
      </div>

    </div>
  );
}

export default DoctorComponent;
