import React, { useState } from 'react';
import './Authentication.css';
import { useNavigate } from 'react-router-dom';
import DoctorComponent from '../Doctor/Doctor';
import PatientComponent from '../Patient/Patient';

function validateAadharId(aadharId) {
  const aadharRegex = /^[2-9]{1}[0-9]{3}\s{1}[0-9]{4}\s{1}[0-9]{4}$/;

  return aadharRegex.test(aadharId);
}

function Authentication() {
  const [showLoginForm, setShowLoginForm] = useState(true);
  const [isDoctor, setIsDoctor] = useState(false);
  const [mintingStatus, setMintingStatus] = useState('');
  const navigate = useNavigate();

  const toggleForm = () => {
    setShowLoginForm((prevState) => !prevState);
    console.log('Toggled');
  };

  // PATIENT FORM SUBMISSION & STORED THE INPUT DATA IN VARIABLES
  const handlePatientFormSubmit = (event) => {
    event.preventDefault();
    const username = event.target.elements.username.value;
    const aadharId = event.target.elements.aadharId.value;
    if (username && aadharId && validateAadharId(aadharId)) {
      console.log('Username:', username);
      console.log('Aadhar ID:', aadharId);
      navigate('/patient');
      setMintingStatus('Minting Successful!');
    } else {
      setMintingStatus('Please enter a valid Aadhar ID or Fill all the fields');
      alert('All fields needs to be perferct')
    }
  };

  // DOCTOR FORM SUBMISSION & STORED THE INPUT DATA IN VARIABLES
  const handleDoctorFormSubmit = (event) => {
    event.preventDefault();
    const name = event.target.elements.name.value;
    const registrationNumber = event.target.elements.registrationNumber.value;
    const registrationYear = event.target.elements.registrationYear.value;
    const smc = event.target.elements.smc.value;
    if (name && registrationNumber && registrationYear && smc) {
      console.log('Name:', name);
      console.log('Registration Number:', registrationNumber);
      console.log('Registration Year:', registrationYear);
      console.log('SMC:', smc);
      setMintingStatus('Minting Successful! Wait for approval.');
      setIsDoctor(true);
    } else {
      setMintingStatus('Fill all the fields');
      alert('All fields are mandatory')
    }
  };

  // Display minting status on console when it is updated
  console.log('Minting Status:', mintingStatus);

  return (
    <>
      {isDoctor ? (
        <DoctorComponent />
      ) : (
        <div className="auth-container">
          <div className="toggle-btn-container">
            <button id="toggle-btn" onClick={toggleForm}>
              {showLoginForm ? '! Patient Registration' : '! Doctor Registration'}
            </button>
            {showLoginForm ? (
              <form onSubmit={handlePatientFormSubmit}>
                <label>
                  User Name:
                  <input type="text" name="username" placeholder="Enter a username" />
                </label>
                <label>
                  Enter Unique ID:
                  <input type="text" name="aadharId" placeholder="37XX 94XX 12XX" />
                </label>
                <button type="submit">User Mint!</button>
              </form>
            ) : (
              <form onSubmit={handleDoctorFormSubmit}>
                <label>
                  Name:
                  <input type="text" name="name" placeholder="Enter Your Name" />
                </label>
                <label>
                  Registration Number:
                  <input type="text" name="registrationNumber" placeholder="Enter Your Registration Number" />
                </label>
                <label>
                  Registration Year:
                  <input type="date" name="registrationYear" placeholder="Enter Registaration Year"  max={new Date().toISOString().split('T')[0]} />
                </label>
                <label>
    State Medical Council:
    <select name="smc" id="smc-select">
      <option value="">-- Select State Medical Council --</option>
      <option value="Andhra Pradesh Medical Council">Andhra Pradesh Medical Council</option>
      <option value="Arunachal Pradesh Medical Council">Arunachal Pradesh Medical Council</option>
      <option value="Assam Medical Council">Assam Medical Council</option>
      <option value="Bihar Medical Council">Bihar Medical Council</option>
      <option value="Chhattisgarh Medical Council">Chhattisgarh Medical Council</option>
      <option value="Goa Medical Council">Goa Medical Council</option>
      <option value="Gujarat Medical Council">Gujarat Medical Council</option>
      <option value="Haryana Medical Council">Haryana Medical Council</option>
      <option value="Himachal Pradesh Medical Council">Himachal Pradesh Medical Council</option>
      <option value="Jammu and Kashmir Medical Council">Jammu and Kashmir Medical Council</option>
      <option value="Jharkhand Medical Council">Jharkhand Medical Council</option>
      <option value="Karnataka Medical Council">Karnataka Medical Council</option>
      <option value="Kerala Medical Council">Kerala Medical Council</option>
      <option value="Madhya Pradesh Medical Council">Madhya Pradesh Medical Council</option>
      <option value="Maharashtra Medical Council">Maharashtra Medical Council</option>
      <option value="Manipur Medical Council">Manipur Medical Council</option>
      <option value="Meghalaya Medical Council">Meghalaya Medical Council</option>
      <option value="Mizoram Medical Council">Mizoram Medical Council</option>
      <option value="Nagaland Medical Council">Nagaland Medical Council</option>
      <option value="Odisha Medical Council">Odisha Medical Council</option>
      <option value="Punjab Medical Council">Punjab Medical Council</option>
      <option value="Rajasthan Medical Council">Rajasthan Medical Council</option>
      <option value="Sikkim Medical Council">Sikkim Medical Council</option>
      <option value="Tamil Nadu Medical Council">Tamil Nadu Medical Council</option>
      <option value="Telangana Medical Council">Telangana Medical Council</option>
      <option value="Tripura Medical Council">Tripura Medical Council</option>
      <option value="Uttar Pradesh Medical Council">Uttar Pradesh Medical Council</option>
      <option value="Uttarakhand Medical Council">Uttarakhand Medical Council</option>
      <option value="West Bengal Medical Council">West Bengal Medical Council</option>
      </select>
  </label>
  <button type="submit">Doctor Mint!</button>
</form>
            )}
          </div>
        </div>
      )}
    </>
  );
}

export default Authentication;
