import React, { useState } from "react";
import PerfectScrollbar from "react-perfect-scrollbar";

const PatientComponent = () => {

  // timeline data
  const timelineData = [
    {
      date: "Jan 2022",
      title: "Diagnosis",
      description: "Lorem ipsum dolor sit amet.",
    },
    {
      date: "Feb 2022",
      title: "Treatment",
      description: "Lorem ipsum dolor sit amet.",
    },
    {
      date: "Mar 2022",
      title: "Recovery",
      description: "Lorem ipsum dolor sit amet.",
    },
    {
      date: "Jan 2022",
      title: "Diagnosis",
      description: "Lorem ipsum dolor sit amet.",
    },
    {
      date: "Feb 2022",
      title: "Treatment",
      description: "Lorem ipsum dolor sit amet.",
    },
    {
      date: "Mar 2022",
      title: "Recovery",
      description: "Lorem ipsum dolor sit amet.",
    },
    {
      date: "Jan 2022",
      title: "Diagnosis",
      description: "Lorem ipsum dolor sit amet.",
    },
    {
      date: "Feb 2022",
      title: "Treatment",
      description: "Lorem ipsum dolor sit amet.",
    },
    {
      date: "Mar 2022",
      title: "Recovery",
      description: "Lorem ipsum dolor sit amet.",
    },
  ];

  // userprofile data
  const [accountAddress] = useState(["Account Address"]);

  const [recentApprovals] = useState([
    "Recent Address 1......",
    "Recent Address 2......",
    "Recent Address 3......",
    "Recent Address 4......",
    "Recent Address 5......",
  ]);

  const [addressToGrantOrRevoke, setAddressToGrantOrRevoke] = useState("");

  const validateAddress = () => {
    const regex = /^0x[a-fA-F0-9]{40}$/;
    return regex.test(addressToGrantOrRevoke);
  };

  const grantPermission = () => {
    if (validateAddress()) {
      console.log("granted for " + addressToGrantOrRevoke);
      alert("Permission granted for the address provided.");
    } else {
      console.log("invalid address");
      alert("Invalid address.");
    }
  };

  const revokePermission = () => {
    if (validateAddress()) {
      console.log("Revoked from " + addressToGrantOrRevoke);
      alert("Permission revoked for the address provided.");
    } else {
      console.log("invalid address");
      alert("Invalid address.");
    }
  };

  return (
    <div className="patient-container">

      {/* PATIENT TIMELINE THAT IS LEFT SIDE */}
      <div className="timeline-container">
        <div className="timeline-header">Timeline of Health Activities</div>

        <PerfectScrollbar className="timeline">
          {timelineData.map((event, index) => (
            <div className="timeline-event" key={index}>
              <div className="event-date">{event.date}</div>
              <div className="event-title">{event.title}</div>
              <div className="event-description">{event.description}</div>
            </div>
          ))}
         </PerfectScrollbar>
      </div>

      {/* PATIENT PROFILE THAT IS RIGHT SIDE */}
      <div className="profile-container">
  <span className="icon-text-container">
    <div className="account_address">
      {this.account_address}
      <i className="material-icons profile-icon">account_circle</i>
    </div>
  </span>

  <div className="profile-content" data-simplebar>
    <h2>Recent Approvals</h2>
    <div className="recent_approval">
      {this.recent_approvals.map((approval, index) => (
        <div key={index}>{approval}</div>
      ))}
    </div>
  </div>

  <input
    type="text"
    className="address-input"
    placeholder="Enter the address to change permission!"
    value={this.state.addressToGrantOrRevoke}
    onChange={this.handleInputChange}
  />

  <div className="button-container">
    <button className="grant-button" onClick={this.grantPermission}>
      Grant
    </button>
    <button className="revoke-button" onClick={this.revokePermission}>
      Revoke
    </button>
  </div>
</div>
</div> ) }


export default PatientComponent;
