import './App.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './Components/Navbar/Navbar';
import HomeComponent from './Components/Home/Home';
import Footer from './Components/Footer/Footer';
import Authentication from './Components/Authentication/Authentication';
import PatientComponent from './Components/Patient/Patient';
import DoctorComponent from './Components/Doctor/Doctor';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import AboutProject from './Components/AboutProject/AboutProject';
import { useState, useEffect } from 'react';

function App() {
  const [hideNavbar, setHideNavbar] = useState(false);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleScroll = () => {
    const currentScrollY = window.scrollY;
    if (currentScrollY > 0) {
      setHideNavbar(true);
    } else {
      setHideNavbar(false);
    }
  };

  return (
    <div>
    <Router>
    {hideNavbar ? null : <Navbar />}
       <ToastContainer /> {/*for showing popup on the doctor page whether the request is sent or not */}
      <Routes>
        <Route path="/" element={<HomeComponent />} />
        <Route path="/about" element={<AboutProject/>} />
        <Route path="/authentication" element={<Authentication />} />
        <Route path="/doctor" element={<DoctorComponent />} />
        <Route path="/patient" element={<PatientComponent />} />
      </Routes>
      <Footer />
    </Router>
    </div>
  );
}

export default App;
